import requests from "./requests.js";
import axios from "axios";

const locations = {
    addLocation: async (userAction, location) => {
        const requestOptions = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'x-auth-token': userAction.token,
                'userId': userAction.userId
            },
            body: JSON.stringify(location)
        };
        const result = await fetch(requests.Locations.AddLocations, requestOptions)
            .then(async response => {
                const isJson = response.headers.get('content-type')?.includes('application/json');
                const data = isJson && await response.json();

                // check for error response
                if (!response.ok) {
                    // get error message from body or default to response status
                    const error = (data && data.errors) || response.status;
                    return Promise.reject(error);
                }
                return data;
                // console.log("data", data)
            })
            .catch(errors => {
                // console.error(errors);
                return { errors }
            });
        return result;
    },
    deleteLocationById: async (userAction, location) => {
        const requestOptions = {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json',
                'x-auth-token': userAction.token,
                'userId': userAction.userId
            },
            body: JSON.stringify(location)
        };
        const result = await fetch(requests.Locations.DeleteLocation, requestOptions)
            .then(async response => {
                const isJson = response.headers.get('content-type')?.includes('application/json');
                const data = isJson && await response.json();

                // check for error response
                if (!response.ok) {
                    // get error message from body or default to response status
                    const error = (data && data.errors) || response.status;
                    return Promise.reject(error);
                }
                return data;
                // console.log("data", data)
            })
            .catch(errors => {
                // console.error(errors);
                return { errors }
            });
        return result;
    },
    getAllLocations: async (userAction) => {
        const requestOptions = {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'x-auth-token': userAction.token,
                'userId': userAction.userId
            }
        };
        const result = await fetch(requests.Locations.GetAll, requestOptions)
            .then(async response => {
                const isJson = response.headers.get('content-type')?.includes('application/json');
                const data = isJson && await response.json();

                // check for error response
                if (!response.ok) {
                    // get error message from body or default to response status
                    const error = (data && data.errors) || response.status;
                    return Promise.reject(error);
                }
                return data;
                // console.log("data", data)
            })
            .catch(errors => {
                // console.error(errors);
                return { errors }
            });
        return result;
    },
    getAllcCountries: async (userAction) => {
        const requestOptions = {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'x-auth-token': userAction.token,
                'userId': userAction.userId
            }
        };
        const result = await fetch(requests.Locations.GetAllCountries, requestOptions)
            .then(async response => {
                const isJson = response.headers.get('content-type')?.includes('application/json');
                const data = isJson && await response.json();

                // check for error response
                if (!response.ok) {
                    // get error message from body or default to response status
                    const error = (data && data.errors) || response.status;
                    return Promise.reject(error);
                }
                return data;
                // console.log("data", data)
            })
            .catch(errors => {
                // console.error(errors);
                return { errors }
            });
        return result;
    },
    getLocationsByCountryId: async (userAction, product) => {

        const requestOptions = {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
                'x-auth-token': userAction.token,
                'userId': userAction.userId
            },
            body: JSON.stringify(product)
        };
        const result = await fetch(requests.Locations.GetAllLocationByCounrtyId, requestOptions)
            .then(async response => {
                const isJson = response.headers.get('content-type')?.includes('application/json');
                const data = isJson && await response.json();

                // check for error response
                if (!response.ok) {
                    // get error message from body or default to response status
                    const error = (data && data.errors) || response.status;
                    return Promise.reject(error);
                }
                return data;
                // console.log("data", data)
            })
            .catch(errors => {
                // console.error(errors);
                return { errors }
            });
        return result;
    },
    createCountriesAndLocations: async (userAction, product) => {

        const requestOptions = {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'x-auth-token': userAction.token,
                'userId': userAction.userId
            },
            body: JSON.stringify(product)
        };
        const result = await fetch(requests.Locations.CreateCountriesAndLocations, requestOptions)
            .then(async response => {
                const isJson = response.headers.get('content-type')?.includes('application/json');
                const data = isJson && await response.json();

                // check for error response
                if (!response.ok) {
                    // get error message from body or default to response status
                    const error = (data && data.errors) || response.status;
                    return Promise.reject(error);
                }
                return data;
                // console.log("data", data)
            })
            .catch(errors => {
                // console.error(errors);
                return { errors }
            });
        return result;
    },
};


export default locations
