import React, { useState, useContext, useEffect } from 'react';
import { AuthContext } from '../../context/auth';
import {
  Box,
  Button,
  MenuItem,
  TextField,
  Typography,
  Grid,
  ListSubheader,
} from '@mui/material';
import { useForm } from '../../hooks/useForm';
import products from './../../requests/products';
import ProductsTable from './ProductsTable';
import DownloadIcon from '@mui/icons-material/Download';
import PageContainer from '../../components/PageContainer';
import AddProductsModal from './AddProductsModal';
import locations from '../../requests/locations';
import { useFetchData } from '../../hooks/useFetchData';
//csv
import { CSVLink } from 'react-csv';
import { ExportDataContext } from './../../context/DataExport';
import TextFieldSelect from '../../components/TextFieldSelect';
import Banner from '../../components/Banner';
import { PageHeaderContainer } from '../../styles/styledComponents';
import GapsTable from './GapsTable';

const ProductsPage = () => {
  const [warehouse, setWarehouse] = useState('');
  const [country, setCountry] = useState('');
  const [addModal, setAddModal] = useState(false);
  const { user, token } = useContext(AuthContext);

  const userAction = {
    userId: user.id,
    token: token,
  };
  const { headers, fileName, data, changeDataExport } =
    useContext(ExportDataContext);

  const {
    state: allproducts,
    error,
    fetchData,
    setState,
  } = useFetchData(products.getAllProducts);
  // console.log('allproducts', allproducts);

  const { state: allCountries } = useFetchData(locations.getAllcCountries);
  // console.log('allCountries', allCountries);

  const { state: allLocations, setState: setLocationState } = useFetchData(
    locations.getAllLocations
  );
  // console.log('allLocations', allLocations);

  const getLocationByCountry = async countryId => {
    const country = {
      countryId: countryId,
    };
    // console.log('country', country);
    const result = await locations.getLocationsByCountryId(userAction, country);
    if (result.length > 0) {
      setLocationState(result);
    } else {
      setLocationState([]);
    }
  };

  useEffect(() => {
    if (country.length !== 0) {
      getLocationByCountry(country);
    }
  }, [country]);

  const { onChange, onSubmit, setFieldValue, values } = useForm(
    searchCallback,
    {
      locationId: '',
      countryId: '',
      search: '',
    }
  );

  const searchProduct = async () => {
    // console.log('values', values);
    const resultSearchProduct = await products.getProductByParams(
      userAction,
      values
    );
    setState(resultSearchProduct);
    // console.log('resultSearchProduct', resultSearchProduct);
  };

  function searchCallback() {
    searchProduct();
  }

  useEffect(() => {
    changeDataExport('Products', allproducts);
  }, [allproducts]);

  const handleAddModal = () => {
    setAddModal(!addModal);
  };

  return (
    <>
      <PageContainer>
        <PageHeaderContainer>
          {country ? <Banner country={country} /> : <Banner country={''} />}

          <Typography sx={{ m: 1 }} variant="h4">
            Products
          </Typography>
          <Box sx={{ m: 1 }}>
            {fileName && headers && data ? (
              <CSVLink
                style={{ textDecoration: 'none', color: '#3F3FBE' }}
                filename={fileName}
                data={data}
                headers={headers}
              >
                <Button
                  variant="outlined"
                  color="secondary"
                  startIcon={<DownloadIcon fontSize="small" />}
                  sx={{ mr: 1 }}
                >
                  Export To Excel
                </Button>
              </CSVLink>
            ) : null}
            {/* {user && user?.name == 'Admin' || user?.name == 'Editor' ? ( */}

            {/* {console.log('asasasas', user)} */}
            {user?.permission !== 'Viewer' ? (
              <Button
                onClick={handleAddModal}
                color="secondary"
                variant="contained"
              >
                Add Product
              </Button>
            ) : null}
          </Box>
        </PageHeaderContainer>

        <Box sx={{ mt: 3 }}>
          <Grid container spacing={2}>
            <Grid item xs={5}>
              <TextField
                fullWidth
                label="Search Product"
                onChange={onChange}
                value={values.search}
                variant="outlined"
                name="search"
              />
            </Grid>
            <Grid item xs={3}>
              <TextFieldSelect
                value={country}
                onChange={event => {
                  setFieldValue('countryId', event.target.value);
                  setCountry(event.target.value);
                }}
                label={'Select country'}
              >
                <MenuItem value="">None</MenuItem>
                <ListSubheader>Select Country</ListSubheader>
                {allCountries
                  ? allCountries.map(country => (
                      <MenuItem
                        key={country.countryId}
                        value={country.countryId}
                      >
                        {country.countryName}
                      </MenuItem>
                    ))
                  : null}
              </TextFieldSelect>
            </Grid>
            <Grid item xs={3}>
              <TextFieldSelect
                value={warehouse}
                onChange={event => {
                  setFieldValue('locationId', event.target.value);
                  setWarehouse(event.target.value);
                }}
                label={'Select Location'}
              >
                <MenuItem value="">None</MenuItem>
                <ListSubheader>Select Location</ListSubheader>
                {allLocations
                  ? allLocations?.map(location => (
                      <MenuItem
                        key={location.locationId}
                        value={location.locationId}
                      >
                        {location.locationName}
                      </MenuItem>
                    ))
                  : null}
              </TextFieldSelect>
            </Grid>
            <Grid sx={{ alignSelf: 'center' }} item xs={1}>
              <Button
                color="secondary"
                variant="outlined"
                onClick={onSubmit}
                sx={{ p: '14px' }}
              >
                Search
              </Button>
            </Grid>
          </Grid>
        </Box>
        {allproducts ? (
          <Box sx={{ mt: 3 }}>
            <ProductsTable
              error={error}
              fetchData={fetchData}
              allproducts={allproducts}
              // allLocations={allLocations}
            />
            <AddProductsModal
              fetchData={fetchData}
              open={addModal}
              close={handleAddModal}
              // allLocations={allLocations}
            />
          </Box>
        ) : null}

        {country || warehouse ? (
          <>
            <PageHeaderContainer sx={{ mt: 5 }}>
              <Typography sx={{ m: 1 }} variant="h4">
                Gaps
              </Typography>
              <Box sx={{ m: 1 }}>
                {/* {fileName && headers && data ? (
              <CSVLink
                style={{ textDecoration: 'none', color: '#3F3FBE' }}
                filename={fileName}
                data={data}
                headers={headers}
              > */}
                <Button
                  variant="outlined"
                  color="secondary"
                  startIcon={<DownloadIcon fontSize="small" />}
                  sx={{ mr: 1 }}
                >
                  Export To Excel
                </Button>
                {/* </CSVLink>
            ) : null} */}
              </Box>
            </PageHeaderContainer>
            <Box sx={{ mt: 3 }}>
              <GapsTable />
            </Box>
          </>
        ) : null}
      </PageContainer>
    </>
  );
};

export default ProductsPage;
