import React from 'react';
import MediaCard from './MediaCard';

let Q = [
  { q: 0, Qnumber: 0, answer: 0 },
  { q: 1, Qnumber: 1, answer: 1 },
  { q: 2, Qnumber: 2, answer: 2 },
  { q: 3, Qnumber: 3, answer: 3 },
  { q: 4, Qnumber: 4, answer: 4 },
  { q: 5, Qnumber: 5, answer: 5 },
  { q: 6, Qnumber: 6, answer: 6 },

];

const q1 = () => {
  let expNume = getRandom2Exponent(18);
  let expResult = getPow(expNume);

  const Qnumber = 1;
  const RandomPointer1 = getRandomPointer();
  const RandomPointer2 = getRandomPointer();
  const numPoiners = expResult / 4;


  let q = `גודל בלוק הוא ${expResult} = 2^${expNume} ומצביע הוא בגודל 4 בתים.
  ישנם ${RandomPointer1} מצביעים ישירים ו-${RandomPointer2} מצביעים עקיפים מסוג single indirect
  מהוא גודל הקובץ המקסימלי במערכת קבצים זו?  
  `;
  const answer = `(${RandomPointer2} + ${RandomPointer1} * ${numPoiners}) * ${expResult} = ` + ((RandomPointer2 + RandomPointer1 * numPoiners) * expResult);
  const result = {
    q: q,
    Qnumber: Qnumber,
    answer: answer,
  };
  console.log(result);
  return result;
}

console.log(q1());
const getQ = () => {
  return Q[(Math.random() * Q.length) | 0];
}
const getRandomPointer = () => {
  return parseInt(Math.random() + 20);
}

const getRandom2Exponent = (max) => {
  return parseInt((Math.random() + 1) * max);
}
const getPow = (pow) => {
  return Math.pow(2, pow)
}

const App = () => {
  return (

    <div style={{ marginTop: '20px' }}>
      <h1>getRandom2Exponent {getRandom2Exponent(15)}</h1>
      <h1>getPow {getPow(10)}</h1>
      <h1>getQ {getQ().q}</h1>

      <MediaCard Qnumber={'1'} q={'refvdfvdvfvvddfv'} answer={'5'} />
    </div>
  );
};

export default App;
