import React, { useContext, useState } from 'react';
import { AuthContext } from '../../context/auth';
import { Modal, Grid, TextField, MenuItem } from '@mui/material';
import ModalHeader from '../../components/ModalHeader';
import MobileDatePicker from '@mui/lab/MobileDatePicker';
import AdapterDateFns from '@mui/lab/AdapterDateFns';
import LocalizationProvider from '@mui/lab/LocalizationProvider';
import { useForm } from '../../hooks/useForm';
import products from '../../requests/products';
import moment from 'moment';
import TextFieldSelect from '../../components/TextFieldSelect';
import { toast } from 'react-toastify';
import Notification from '../../components/Notification';
import StyledModal from '../../components/StyledModal';
import { useFetchData } from '../../hooks/useFetchData';
import locations from '../../requests/locations';
const specialProducts = [
  'acom amplifier',
  'barret transceiiver',
  'exodus aplifier gen1',
  'tujicom amplifier gen1',
  'tujicom amplifier gen2',
];

const AddProductsModal = ({ open, close, fetchData }) => {
  const [warehouse, setWarehouse] = useState('');
  const [status, setStatus] = useState('');
  const { user, token } = useContext(AuthContext);
  const userAction = {
    userId: user.id,
    token: token,
  };
  const { state: allLocations, setState: setLocationState } = useFetchData(
    locations.getAllLocations
  );
  const { onChange, onSubmit, setFieldValue, values } = useForm(
    addProductCallback,
    {
      productName: '',
      serialNumber: '',
      PONumber: '',
      warranty: moment(new Date()).format('YYYY-MM-DD'),
      description: '',
      quantity: 1,
      locationId: '',
      status: '',
    }
  );
  const emptyVal = () => {
    setFieldValue('productName', '');
    setFieldValue('serialNumber', '');
    setFieldValue('PONumber', '');
    setFieldValue('quantity', 1);
    setFieldValue('locationId', '');
    setFieldValue('status', '');
    setFieldValue('warranty', moment(new Date()).format('YYYY-MM-DD'));
  }
  const addProduct = async () => {
    let err = false;
    if (values.productName.length === 0) {
      toast.error(
        <Notification text={'Please include product name for product'} />
      );
      err = true;
    }
    if (values.locationId.length === 0) {
      toast.error(
        <Notification text={'Please include location for product'} />
      );
      err = true;
    }


    if (specialProducts.includes(values.productName.toLowerCase())) {
      if (values.status.length === 0) {
        toast.error(
          <Notification text={'Please include status for product'} />
        );
        err = true;
      }
      if (values.serialNumber.length === 0) {
        toast.error(
          <Notification text={'Please include serialNumber for product'} />
        );
        err = true;
      }
    }


    if (!err) {
      const run = (values.serialNumber !== '') ? 1 : parseInt(values.quantity)
      // console.log('run', run);

      for (let index = 0; index < run; index++) {
        const resultAddProduct = await products.addProduct(userAction, values);

        if (resultAddProduct?.errors) {
          console.log('resultAddProduct.errors', JSON.stringify(resultAddProduct.errors));
          toast.error(<Notification text={'error'} />);
        } else {
          emptyVal();
          toast.success(<Notification text={'Product added'} />);
          fetchData();
          close();
        }
        // console.log('resultAddProduct', resultAddProduct);
      }
    }
  };

  function addProductCallback() {
    addProduct();
  }

  // console.log(values);

  return (
    <LocalizationProvider dateAdapter={AdapterDateFns}>
      <Modal open={open} onClose={close}>
        <StyledModal submitText={'Add Product'} onSubmit={onSubmit}>
          <ModalHeader title="Add Product" close={close} />

          <Grid container spacing={3}>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Product Name"
                onChange={onChange}
                value={values.productName}
                variant="outlined"
                name="productName"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="S/N of Product"
                onChange={onChange}
                value={values.serialNumber}
                variant="outlined"
                name="serialNumber"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="PO Number"
                onChange={onChange}
                value={values.PONumber}
                variant="outlined"
                name="PONumber"
              />
            </Grid>
            <Grid item xs={12}>
              <MobileDatePicker
                label="Warranty"
                openTo="year"
                views={['year', 'month', 'day']}
                value={values.warranty}
                name="warranty"
                onChange={newValue => {
                  setFieldValue(
                    'warranty',
                    moment(newValue).format('YYYY-MM-DD')
                  );
                }}
                renderInput={params => <TextField fullWidth {...params} />}
              />
            </Grid>
            {allLocations ? (
              <Grid item xs={12}>
                <TextFieldSelect
                  value={warehouse}
                  onChange={event => {
                    setWarehouse(event.target.value);
                    setFieldValue('locationId', event.target.value);
                  }}
                  label="Select location"
                >
                  <MenuItem disabled value={'Select location'}>
                    Select location
                  </MenuItem>
                  {allLocations.map(location => (
                    <MenuItem
                      key={location.locationId}
                      value={location.locationId}
                    >
                      {location.locationName}
                    </MenuItem>
                  ))}
                </TextFieldSelect>
              </Grid>
            ) : null}
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Comment"
                onChange={onChange}
                value={values.description}
                variant="outlined"
                multiline
                rows={4}
                name="description"
              />
            </Grid>

            {!specialProducts.includes(values.productName.toLowerCase()) &&
              values.serialNumber === '' ? (
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Quantity"
                  onChange={onChange}
                  value={values.quantity}
                  variant="outlined"
                  name="quantity"
                />
              </Grid>
            ) : null}

            {specialProducts.includes(values.productName.toLowerCase()) ? (
              <Grid item xs={12}>
                <TextFieldSelect
                  value={status}
                  onChange={event => {
                    setStatus(event.target.value);
                    setFieldValue('status', event.target.value);
                  }}
                  label={'Status'}
                >
                  <MenuItem disabled value={'Select Status'}>
                    Select Status
                  </MenuItem>
                  <MenuItem value="Fault">Fault</MenuItem>
                  <MenuItem value="Operational">Operational</MenuItem>
                  <MenuItem value="Awaiting ATP">Awaiting ATP</MenuItem>
                </TextFieldSelect>
              </Grid>
            ) : null}
          </Grid>
        </StyledModal>
      </Modal>
    </LocalizationProvider>
  );
};

export default AddProductsModal;
